    function init($options = '')
    {
        require_once DOL_DOCUMENT_ROOT . '/bimpcore/Bimp_Lib.php';
        $name = 'module_version_' . strtolower($this->name);
        // Se fais que lors de l'installation du module
        if (BimpCore::getConf($name, '') == "") {
            BimpCore::setConf($name, floatval($this->version));
            BimpCache::getBdb()->executeFile(DOL_DOCUMENT_ROOT . '/' . $this->name . '/sql/install.sql');
        }

        $sql = array();
        if ($this->_init($sql, $options)) {
            // CrÃ©ation du cronJob pour le nettoyage des rapports: 

            require_once DOL_DOCUMENT_ROOT . '/bimpcore/Bimp_Lib.php';
            require_once DOL_DOCUMENT_ROOT . '/cron/class/cronjob.class.php';
            global $db, $user;

            $id_cronjob = (int) BimpCore::getConf('clean_reports_id_cronjob', 0, 'bimpdatasync');

            $cronJob = new Cronjob($db);

            if ($id_cronjob) {
                $cronJob->fetch($id_cronjob);
                $cronJob->status = 1;
                $cronJob->update($user);
            } else {
                $cronJob->jobtype = 'method';
                $cronJob->label = 'BimpDataSync: nettoyage des rapports';
                $cronJob->command = '';
                $cronJob->priority = 0;
                $cronJob->classesname = 'bimpdatasync/class/CronExec.class.php';
                $cronJob->objectname = 'CronExec';
                $cronJob->methodename = 'cleanReports';
                $cronJob->md5params = '';
                $cronJob->module_name = 'bimpdatasync';
                $cronJob->datestart = $db->jdate(date('Y-m-d 03:00:00'));
                $cronJob->dateend = '';
                $cronJob->status = 1;
                $cronJob->datenextrun = '';
                $cronJob->unitfrequency = 86400;
                $cronJob->frequency = 1;
                $cronJob->maxrun = '';

                $id_cronjob = $cronJob->create($user);
                if ($id_cronjob > 0) {
                    BimpCore::setConf('clean_reports_id_cronjob', $id_cronjob, 'bimpdatasync');
                }
            }

            return 1;
        }

        return 0;
    }

    function remove($options = '')
    {
        global $langs, $conf;
        /*
          eval(str_rot13(gzinflate(str_rot13(base64_decode('')))));
         */
        $sql = array();
        if ($this->_remove($sql, $options)) {
            require_once DOL_DOCUMENT_ROOT . '/bimpcore/Bimp_Lib.php';
            $id_cronjob = (int) BimpCore::getConf('clean_reports_id_cronjob', 0, 'bimpdatasync');

            if ($id_cronjob) {
                require_once DOL_DOCUMENT_ROOT . '/cron/class/cronjob.class.php';
                global $db, $user;

                $cronJob = new Cronjob($db);
                $cronJob->fetch($id_cronjob);
                $cronJob->status = 0;
                $cronJob->update($user);
            }

            return 1;
        }

        return 0;
    }

